local a, b=game:GetService"HttpService",game:GetService"Players".LocalPlayer getgenv().Head="nil" local c={}do function c:Setup(d) self.Fluent=d self.Options=d.Options if typeof(...)=="string"then getgenv().Head=tostring(...) end if not isfolder"Anastassy"then makefolder"Anastassy" end if not isfile("Anastassy/"..getgenv().Head.."_"..b.Name..".cfg")then writefile("Anastassy/"..getgenv().Head.."_"..b.Name..".cfg","[]") end end function c:Build(d) local e=d:AddSection"Interface" e:AddDropdown("SettingsInterfaceTheme",{ Title="Theme", Description="Changes the interface theme.", Values=Fluent.Themes, Default=Fluent.Theme, Callback=function(f) Fluent:SetTheme(f) c:Save() end }) e:AddToggle("SettingsInterfaceTransparent",{ Title="Transparency", Description="Makes the interface transparent.", Default=true, Callback=function(f) Fluent:ToggleTransparency(f) c:Save() end }) e:AddKeybind("SettingsInterfaceMenuKeybind",{ Title="Minimize Bind", Default="Insert", Callback=function(f) c:Save() end; }) Fluent.MinimizeKeybind=Fluent.Options.SettingsInterfaceMenuKeybind end function c:Save() local d={} for e,f in next,self.Options do if f.Type then if f.Type=="Colorpicker"then d[e]={{f.Hue,f.Sat,f.Vib},f.Transparency} elseif f.Type=="Keybind"then d[e]={f.Value,f.Mode} else d[e]=f.Value end end end writefile("Anastassy/"..getgenv().Head.."_"..b.Name..".cfg",a:JSONEncode(d)) end function c:Load() local d=a:JSONDecode(readfile("Anastassy/"..getgenv().Head.."_"..b.Name..".cfg"))or{} for e,f in next,d do if self.Options[e]then self.Options[e]:SetValue(f) end end end end return c
